import React from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import './index.css';
import { Button, Tree } from 'antd';

class Call extends React.Component{
  constructor(props)
    {
      super(props)
      this.state =
      {
        image: "",
        image_check :false,
      }
      this.get_data_all()
      this.handleClick = this.handleClick.bind(this)

  }
  handleClick(){
    this.setState({image_check:true})
  }
  get_data_all = async()=>
    {
        const path = "http://localhost:9000/Send_data/";
        await axios(
            {
              method :'get',
              url: "http://localhost:9000/Send_data/image 1",

            }
        ).then((res)=>this.setState({image:res.data}));
    }
    set_image = ()=>
    {
        if(this.state.image_check)
        {
            return(
                <div>
                    <div style={{display: 'flex',  justifyContent:'center', alignItems:'center',height:"70%",flexDirection: "vertical",backgroundColor :"white",marginTop:"50px",border :"30px solid white",marginBottom:"50px",marginTop:"50px"}}>
                        <img src={"data:image/png[jpg];base64,"+this.state.image.toString()} style = {{height:500}}/>
                    </div>
                </div>
            )
        }
    }

  render() {
    return (
      <div>
      <div style={{display: 'flex',justifyContent:'center', alignItems:'center',flexDirection: "vertical",fontSize: 10 ,backgroundColor:"white", marginTop :'20px'}}>
        <Button 
          type="primary"
          onClick={this.handleClick}>
          Loading
        </Button>
      </div>
        {this.set_image()}
      </div>
    );
  }
}

export default Call;
