import React, { Component } from "react";
import Chart from "react-apexcharts";
import { Button } from 'antd';
import * as dayjs from 'dayjs' ;
let candle_data = require('./csvjson.json');

class APEX extends Component {
  constructor(props) {
    super(props);
    this.state = {
      series:[{data:[]}],
      options: {
        chart: {
          type: 'candlestick',
          height: 500
        },
        xaxis: {
          type: 'category',  
        labels: {
          formatter: function(val) {
            return dayjs(val).format('ddd DD MMM HH:mm');
          },
        },
      },
        yaxis: {
          tooltip: {
            tickAmount: 6,
                  min: 16,
                  max: 30,
            enabled: true
          }
        },
        title: {
          text: 'CandleStick Chart',
          align: 'center'
        },
        plotOptions: {
          candlestick: {
            colors: {
              upward: '#3C90EB',
              downward: '#DF7D46'
            }
          }
        }
      },
    };
  }
  set1_HOUR =()=>{
    var temp=[];
    this.setState({
      series:[{data:[]}]
    })
    for(let i = 0 ; i < candle_data.length ;i++){
      let tempdate = candle_data[i].date;
      tempdate = tempdate.split(".");
      let temphr = candle_data[i].hr;
      temphr = temphr.split(".");
      let date = new Date();
      date.setHours(parseInt(temphr[0].split(":")[0]));
      date.setMinutes(parseInt(temphr[0].split(":")[1]));
      date.setMonth(parseInt(tempdate[1]-1));
      date.setFullYear(parseInt(tempdate[0]));
      date.setDate(parseInt(tempdate[2])); 
      let tempall = {x:date,y:[candle_data[i].open,candle_data[i].high,candle_data[i].low,candle_data[i].close]};
      temp.push(tempall);
    } 
    this.setState({
      series:[{data:temp}]
    })
}
set6_HOUR =()=>{
  var temp=[];
  this.setState({
    series:[{data:[]}]
  })
  for(let i = 0 ; i < candle_data.length ;i++){
    let tempdate = candle_data[i].date;
    tempdate = tempdate.split(".");
    let temphr = candle_data[i].hr;
    temphr = temphr.split(".");
    let date = new Date();
    date.setHours(parseInt(temphr[0].split(":")[0]));
    date.setMinutes(parseInt(temphr[0].split(":")[1]));
    date.setMonth(parseInt(tempdate[1]-1));
    date.setFullYear(parseInt(tempdate[0]));
    date.setDate(parseInt(tempdate[2])); 
    
    if(i != 0){
      if(i %6 == 0){
        let tempall = {x:date,y:[open,maxhigh,minlow,candle_data[i-1].close]};
        temp.push(tempall);
        open = candle_data[i].open;
        minlow = candle_data[i].low;
        maxhigh = candle_data[i].high;
      }
      
      else{
        if(candle_data[i].high > maxhigh){
          maxhigh = candle_data[i].high;
        }
        if(candle_data[i].low < minlow){
          minlow = candle_data[i].low;
        }
        
      }
    }
    else if(i == 0){
      var open = candle_data[0].open;
      var maxhigh = candle_data[0].high;
      var minlow = candle_data[0].low;
    }
    
  }
  this.setState({
    series:[{data:temp}]
  })
}
